              <h1>{currentPosts[key][key]?.product_name}</h1>
              <Pagination postsPerPage={postsPerPage} totalPosts={posts.length} paginate={paginate} />
              {currentPosts[key].map((dataItem,index) => {
                     return (    <>


                 <li key={index}>{dataItem.product_name}


                 </li>


                </>
                     )
               })}

           </div>

         )
       })}


