import React,{useState,useEffect} from 'react';
import './App.css';
import axios from 'axios';
import Posts from './components/Posts';
import Pagination from './components/Pagination';



function App() {

  const [posts,setPosts]=useState([]);
  const [loading,setLoading]= useState(false);
  const [product,Setproduct] = useState('0');
  const [city,SetCity] = useState('0');
  const [currentPage,setCurrentPage]=useState(1);
  const [postsPerPage]=useState(10);

  const fetchPosts = async ()=>{
    setLoading(true);
    const res = await axios.get('https://assessment-edvora.herokuapp.com');
    setPosts(res.data);
    setLoading(false);
  }
  useEffect(()=>{
    fetchPosts();
  },[]); 

  function compareName(a, b) {

    // converting to uppercase to have case-insensitive comparison
    const product_name1 =  a.product_name.toUpperCase();
    const product_name2 =  b.product_name.toUpperCase();

    let comparison = 0;

    if (product_name1 > product_name2) {
        comparison = 1;
    } else if (product_name1 < product_name2) {
        comparison = -1;
    }
    return comparison;
}

function group(posts) {
  let map = new Map(posts.map(o => [o.product_name, []]));
  for (let o of posts) map.get(o.product_name).push(o);

  return [
      ...[...map.values()].filter(({length}) => length > 1),
      [...map.values()].filter(({length}) => length === 1).flat()

  ];


}

  //Get current posts

  // let indexOfLastPost = currentPage *  postsPerPage;
  // const indexOfFirstPost = indexOfLastPost - postsPerPage;
  // const currentPosts  = group(posts.sort(compareName)).slice(indexOfFirstPost,indexOfLastPost);

  const currentPosts  = group(posts.sort(compareName));

  // Change Page

  // const  paginate = (pageNumber) => setCurrentPage(pageNumber);

  // Filter Product Name
  const seen = new Set();
  const filteredArr = posts.filter(el => {
    const duplicate = seen.has(el.product_name);
    seen.add(el.product_name);
    return !duplicate;
  });


  const filteredArray = posts.filter(function (el){
    let value=0;
    if(posts==='0')
    {
      value =  el.product_name===product  
    }
      return value

  });


  return (

    <div>


      App
      <div>

        {console.log('product',product)}

  <label htmlFor="products">Prodcuts</label>
  <select name="products" id="products" onChange={(e)=>{

    Setproduct(e.target.value)}

    }>
  <option value={0}>Products</option>   
    {filteredArr.map((fil,index)=>{
      return  <option  key={index} value={fil.product_name}>{fil.product_name}</option>
    })}

  </select>
  <br/><br/>


  <label htmlFor="city">City</label>
  <select name="city" id="city" onChange={(e)=>{

    SetCity(e.target.value)} 
    }>
  <option value={0}>Products</option>   
    {filteredArr.map((fil,index)=>{
      return  <option  key={index} value={fil.product_name}>{fil.product_name}</option>
    })}

  </select>
  <br/><br/>


      </div>
      {product==='0'?
          Object.keys(currentPosts).map((key) => {


         return (
          <div key={key}>

          <h1>{currentPosts[key][key]?.product_name}</h1>


             <Posts  posts={currentPosts[key]} loading={loading}/>  

       </div>





         )
       })
      :

      <div>
        hey

       {console.log('filteredArray',filteredArray)}
       <h1>{filteredArray[1].product_name}</h1>
         <Posts  posts={filteredArray} loading={loading}/> 
      </div>
      }


    </div>
  );
}

