import React from 'react'

function Posts({posts,loading}) {


    var valueArr = posts.map(function(item){ return item.product_name });
    var isDuplicate = valueArr.some(function(item, idx){ 
        return valueArr.indexOf(item) !== idx 
    });

    function group(posts) {
        let map = new Map(posts.map(o => [o.product_name, []]));
        for (let o of posts) map.get(o.product_name).push(o);

       let arr = [
            ...[...map.values()].filter(({length}) => length > 1),
            [...map.values()].filter(({length}) => length === 1).flat()

        ];
        return <div>
            {arr.map((post,index)=>{

              return <li key={index}>

                 {post.product_name}
                 {' '}
                 {post.brand_name}
                 {' '}
                 {post.price}
                 {' '}
                 {post.brand_name}
                </li>  


         })
        }
        </div>
    }


    if(loading){
        return <h2>Loading...</h2>
    }


    return (
        <ul>
            {/* {console.log('posts',posts)}
            {console.log('valueArr',valueArr)} */}
       {console.log('isDuplicate',isDuplicate)}
       {console.log('group',group(posts))}

     {isDuplicate ?

            posts.map((post,index)=>{

                 return <li key={index}>

                    {post.product_name}
                    {' '}
                    {post.brand_name}
                    {' '}
                    {post.price}
                    {' '}
                    {post.brand_name}
                   </li>  


            }
                )       
                :<div>
                    hey
                </div>
                }
        </ul>
    )
}

export default Posts
