  const [currentPage,setCurrentPage]=useState(1);
  const [postsPerPage]=useState(2);

  const fetchPosts = async ()=>{
    setLoading(true);
    const res = await axios.get('https://assessment-edvora.herokuapp.com');
    setPosts(res.data);
    setLoading(false);
  }
  useEffect(()=>{
    fetchPosts();
  },[]); 

  function compareName(a, b) {

