import React,{useState} from 'react'

function Posts({posts,loading}) {


    var valueArr = posts.map(function(item){ return item.brand_name });
    var isDuplicate = valueArr.some(function(item, idx){ 
        return valueArr.indexOf(item) !== idx 
    });

    function compareName(a, b) {

        // converting to uppercase to have case-insensitive comparison
        const product_name1 = valueArr.product_name;
        const product_name2 = valueArr.product_name;

        let comparison = 0;

        if (product_name1 > product_name2) {
            comparison = 1;
        } else if (product_name1 < product_name2) {
            comparison = -1;
        }
        return comparison;
    }

    console.log(compareName,posts.sort(compareName));
    if(loading){
        return <h2>Loading...</h2>
    }
    return (
        <ul>
            {console.log('posts',posts)}
            {console.log('valueArr',valueArr)}
       {console.log('isDuplicate',isDuplicate)}

            {posts.sort((a, b) => (a.product_name === b.product_name) ? 1 : -1)}

            {posts.map((post,index)=>(

                 <li key={index}>

                {post.product_name}
                {' '}
                {post.brand_name}
                {' '}
                {post.price}
                {' '}
                {post.brand_name}
               </li>    
            )
                )}
        </ul>
    )
}

export default Posts
