import React,{useState} from 'react'
import Pagination from './Pagination';

function Posts({posts,loading}) {


    // var valueArr = posts.map(function(item){ return item.product_name });
    // var isDuplicate = valueArr.some(function(item, idx){ 
    //     return valueArr.indexOf(item) !== idx 
    // });



    function group(posts) {
        let map = new Map(posts.map(o => [o.product_name, []]));
        for (let o of posts) map.get(o.product_name).push(o);

       let arr = [
            ...[...map.values()].filter(({length}) => length > 1),
            [...map.values()].filter(({length}) => length === 1).flat()

        ];
        return <div>


    <div>
      {Object.keys(arr).map((key) => {
         return (
           <div key={key}>

              <h1>{arr[key][key]?.product_name}</h1>
              {arr[key].map((dataItem,index) => {
                return (
                 <li key={index}>{dataItem.product_name}</li>
                )
               })}
           </div>
         )
       })}
     </div>

        </div>
    }
    const [currentPage,setCurrentPage]=useState(1);
  const [postsPerPage]=useState(10);

    //Get current posts

    let indexOfLastPost = currentPage *  postsPerPage;
    const indexOfFirstPost = indexOfLastPost - postsPerPage;
    const currentPosts  = posts.slice(indexOfFirstPost,indexOfLastPost);

    const  paginate = (pageNumber) => setCurrentPage(pageNumber);


    if(loading){
        return <h2>Loading...</h2>
    }


    return (
        <>
        <ul>

            {group(posts)}

        </ul>
        <Pagination postsPerPage={postsPerPage} totalPosts={posts.length} paginate={paginate} />
        </>

    )
}

export default Posts
